        showConfirmButton: true,
        showInput: true,
        //是否显示输入框
        inputValue: "",
        //输入框默认值
        inputType: "text",
        //输入框的类型
        inputPlaceholder: "请输入年龄" //输入框默认占位符

      }).then(function (action) {
        console.log(action);
      });
